#!/bin/bash
#
# run automatic/lod-materialize-dataone-dataone-objects-2013-May-29.sh
# from dataone/dataone-objects/version/2013-May-29/

CSV2RDF4LOD_HOME=${CSV2RDF4LOD_HOME:?"not set; source csv2rdf4lod/source-me.sh or see https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD-not-set"}

delete=""
if [ ! -e publish/dataone-dataone-objects-2013-May-29.nt ]; then
  delete="publish/dataone-dataone-objects-2013-May-29.nt"
  if [ -e publish/dataone-dataone-objects-2013-May-29.nt.gz ]; then
    gunzip -c $allNT.gz > $allNT
  elif [ -e publish/dataone-dataone-objects-2013-May-29.ttl ]; then
    echo "cHuNking publish/dataone-dataone-objects-2013-May-29.ttl into publish/dataone-dataone-objects-2013-May-29.nt; will delete when done lod-mat'ing"
    $CSV2RDF4LOD_HOME/bin/util/bigttl2nt.sh publish/dataone-dataone-objects-2013-May-29.ttl > publish/dataone-dataone-objects-2013-May-29.nt
  elif [ -e publish/dataone-dataone-objects-2013-May-29.ttl.gz ]; then
    gunzip -c $allTTL.gz > $allTTL
    echo "cHuNking publish/dataone-dataone-objects-2013-May-29.ttl into publish/dataone-dataone-objects-2013-May-29.nt; will delete when done lod-mat'ing"
    $CSV2RDF4LOD_HOME/bin/util/bigttl2nt.sh publish/dataone-dataone-objects-2013-May-29.ttl > publish/dataone-dataone-objects-2013-May-29.nt
    rm publish/dataone-dataone-objects-2013-May-29.ttl
  else
    echo publish/dataone-dataone-objects-2013-May-29.nt, publish/dataone-dataone-objects-2013-May-29.nt.gz, publish/dataone-dataone-objects-2013-May-29.ttl, or publish/dataone-dataone-objects-2013-May-29.ttl.gz needed to lod-materialize.
    delete=""
    exit 1
  fi
fi

                # The newer C version of lod-mat is faster.
c_lod_mat="c/"  # It is in the directory called 'c' within the lod-materialization project.
                # The C version silently passes some parameters that the native perl version used.
if [ ! -e $CSV2RDF4LOD_HOME/bin/lod-materialize/c/lod-materialize ]; then
   c_lod_mat="" # If it is not available, use the older perl version.
   echo "WARNING: REALLY SLOW lod-materialization going on. Run make in $CSV2RDF4LOD_HOME/bin/lod-materialize/c/"
fi

writeBuffer="--buffer-size=${CSV2RDF4LOD_PUBLISH_LOD_MATERIALIZATION_WRITE_FREQUENCY:-"1000000"}"
humanReport="--progress=${CSV2RDF4LOD_PUBLISH_LOD_MATERIALIZATION_REPORT_FREQUENCY:-"10000"}"
concurrency="--concurrency=${CSV2RDF4LOD_CONCURRENCY:-"1"}"
freqParams=" $writeBuffer $humanReport $concurrency "

# -D namespace abbreviations, -p: print progress
perl $CSV2RDF4LOD_HOME/bin/lod-materialize/${c_lod_mat}lod-materialize.pl -i=ntriples -D base_vocab=https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD_BASE_URI#/vocab/ -D bibo=http://purl.org/ontology/bibo/ -D coin=http://purl.org/court/def/2009/coin# -D con=http://www.w3.org/2000/10/swap/pim/contact# -D conversion=http://purl.org/twc/vocab/conversion/ -D dataone-objects=https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD_BASE_URI#/source/dataone/dataset/dataone-objects/version/2013-May-29/ -D dataone-objects_global_value=https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD_BASE_URI#/source/dataone/dataset/dataone-objects/ -D dataone-objects_vocab=https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD_BASE_URI#/source/dataone/dataset/dataone-objects/vocab/ -D dataone_vocab=https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD_BASE_URI#/source/dataone/vocab/ -D dbpedia=http://dbpedia.org/resource/ -D dbpediaowl=http://dbpedia.org/ontology/ -D dbpediaprop=http://dbpedia.org/property/ -D dcat=http://www.w3.org/ns/dcat# -D dcterms=http://purl.org/dc/terms/ -D dgtwc=http://data-gov.tw.rpi.edu/2009/data-gov-twc.rdf# -D doap=http://usefulinc.com/ns/doap# -D e1=https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD_BASE_URI#/source/dataone/dataset/dataone-objects/vocab/enhancement/1/ -D foaf=http://xmlns.com/foaf/0.1/ -D frbrcore=http://purl.org/vocab/frbr/core# -D geonames=http://www.geonames.org/ontology# -D geonamesid=http://sws.geonames.org/ -D govtrackusgov=http://www.rdfabout.com/rdf/usgov/geo/us/ -D muo=http://purl.oclc.org/NET/muo/muo# -D nfo=http://www.semanticdesktop.org/ontologies/2007/03/22/nfo# -D ov=http://open.vocab.org/terms/ -D owl=http://www.w3.org/2002/07/owl# -D prov=http://www.w3.org/ns/prov# -D qb=http://purl.org/linked-data/cube# -D raw=https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD_BASE_URI#/source/dataone/dataset/dataone-objects/vocab/raw/ -D rdf=http://ww